import React, { useContext, useState, useEffect } from 'react';
import { Eye4eyeContext } from '../context/Eye4eyeContext';
import { networks } from '../utils/connect';


const Navbar = () => {
  const { connectWallet, getCurrentAccount, getCurrentNetwork } = useContext(Eye4eyeContext);
  const [accountAddress, setAccountAddress] = useState('');
  const [currentNetwork, setCurrentNetwork] = useState('');

  useEffect(() => {
    const currentAccount = getCurrentAccount();
    console.log('currentAccount:', currentAccount);
    const currentNetwork = getCurrentNetwork();
    if (currentAccount) {
      setAccountAddress(currentAccount.slice(0, 6) + '...' + currentAccount.slice(-4));
    } else {
      setAccountAddress('unconnected');
    }
    setCurrentNetwork(currentNetwork);
  }, [getCurrentAccount, getCurrentNetwork]);

  return (
    <nav>
      <div className="logo">
        <h2>
          <a href="/">DeDeal</a>
        </h2>
      </div>
      <ul className="navLinks">
        {/* <li><a href="https://dedeal.site/" target="_blank" rel="noreferrer">Testnet</a></li> */}
        {/* <li>
          <a href="https://twitter.com/DeDealOfficial" target="_blank" rel="noreferrer">
            Twitter
          </a>
        </li> */}
        {/* <li>
          <a href="https://polygonscan.com/address/0x53a91e89fcd66d166ac538dd78dc82948574bde7" target="_blank" rel="noreferrer">
            Polyscan
          </a>
        </li> */}
        {/* <li>
          <a href="https://dedeal.gitbook.io/whitepaper/" target="_blank" rel="noreferrer">
            White Paper:EN
          </a>
        </li>
        <li>
          <a href="https://dedeal.gitbook.io/whitepaper/v/ja/" target="_blank" rel="noreferrer">
            White Paper:JP
          </a>
        </li> */}
      </ul>
      <div className="wallet-info">
        <span className='wallet-address'>
          {networks[currentNetwork] ? networks[currentNetwork]?.name : "change network"}
        </span>
        <span className="wallet-address">{accountAddress}</span>
        <button type="button">
          <p className="buttonText" onClick={connectWallet}>
            Connect Wallet
          </p>
        </button>
      </div>
    </nav>
  );
};

export default Navbar;
